import { ArrowLeftOutlined } from '@ant-design/icons';
import { Avatar, Col, Image, Row, Typography } from 'antd';
import Loading from 'components/Loading';
import {
  movieActions,
  selectConfigImg,
  selectDetailMovie,
  selectLoading,
} from 'features/movie/movieSlice';
import { useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAppDispatch, useAppSelector } from 'store/hook';
import { convertToDate, toHoursAndMinutes } from 'utils/functions';

const { Title, Link } = Typography;

const MovieDetail = () => {
  const dispatch = useAppDispatch();
  const detailMovie = useAppSelector(selectDetailMovie);
  const configImg = useAppSelector(selectConfigImg);
  const isLoading = useAppSelector(selectLoading);

  const navigate = useNavigate();

  const param = useParams<{ id: string }>();

  useEffect(() => {
    if (param.id) {
      dispatch(movieActions.fetchDetailMovie(param.id!));
    }
  }, [dispatch, param.id]);

  const { hours, minutes } = toHoursAndMinutes(detailMovie.runtime);

  const goBack = () => {
    navigate(-1);
  };

  if (isLoading) {
    return <Loading />;
  }

  return (
    <div className='detail-movie-page'>
      <Link onClick={goBack} className='back'>
        <ArrowLeftOutlined style={{ marginRight: 4 }} />
        Back List
      </Link>

      <div
        className='movie-background'
        style={{
          backgroundImage: `url(${configImg.images.base_url}original${detailMovie.backdrop_path})`,
        }}
      >
        <Row className='movie-content'>
          <Col lg={6} xs={24}>
            <Image
              fallback='data:image/png;base64,'
              src={`${
                detailMovie.poster_path
                  ? configImg.images.base_url +
                    'w300_and_h450_bestv2' +
                    detailMovie.poster_path
                  : 'error'
              }`}
              srcSet={`${
                detailMovie.poster_path
                  ? configImg.images.base_url +
                    'w300_and_h450_bestv2' +
                    detailMovie.poster_path +
                    ' 1x, ' +
                    configImg.images.base_url +
                    'w600_and_h900_bestv2' +
                    detailMovie.poster_path +
                    ' 2x'
                  : ''
              }`}
            />
          </Col>
          <Col lg={18} xs={24} className='movie-info'>
            <Title style={{ marginTop: 20, color: '#fff' }}>
              {detailMovie.original_title}
            </Title>
            <div className='movie-facts'>
              <span>{convertToDate(detailMovie.release_date)}</span>
              <span>
                {detailMovie.genres.map((item) => item.name).join(', ')}
              </span>
              <span>
                {hours}h {minutes}m
              </span>
            </div>
            <div className='tag-line'>{detailMovie.tagline}</div>
            <div className='overview'>
              <Title level={4} style={{ color: '#fff' }}>
                Overview
              </Title>
              {detailMovie.overview}
            </div>
            <div className='companies'>
              <Title level={4} style={{ color: '#fff' }}>
                Companies
              </Title>
              <ul>
                {detailMovie.production_companies.map((item) => (
                  <li key={item.id}>
                    <Avatar
                      src={`${
                        item.logo_path
                          ? configImg.images.base_url + 'w92' + item.logo_path
                          : ''
                      }`}
                    />
                    {item.name}
                  </li>
                ))}
              </ul>
            </div>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default MovieDetail;
